$(function(){
		//페이지 불러올 때 페이지 리스트 가져오기
		$.getJSON({
			url:"/store/storeList",
			success:function(data){
				
				console.log(data);
				
				var str="";
				
				$(data).each(function(idx, obj){
					str+="<li id='list"+idx+"' class='list-group-item' data-code='"+obj.code+"'>"+obj.name+"</li>";
				})
				$(".menu").append(str);
				
				$("#list0").attr("class", "list-group-item activated");
				var code = $("#list0").data("code");
				console.log("code : "+code);
				setStoreInfo(code);
			}
		})
		
		function setStoreInfo(code){
		
		$.getJSON({
			url:"/store/getStore",
			data:{
				code:code
			},
			success:function(data){
				
				console.log("data : "+data);
				
				var str = "";
				
				$(data.attachList).each(function(idx, obj){
					var fileCallPath = encodeURIComponent(obj.uploadPath+"\\"+obj.uuid+"_"+obj.fileName);
					str+="<img class='rounded store-img' alt='' src='/upload/display?fileName="+fileCallPath+"' style='width: 100%; height: auto'>"
				})
				
				str+="<h3 class='name' style='font-size: 1em; margin-top: 1em'>"+data.name+"</h3>"	
				str+="<h3 class='address' style='font-size: 1em; margin-top: 1em'>"+data.address+"</h3>"
				
				$(".storeInfo").html(str);
				
				searchMap();
			}
		})
	}
		
		//리스트 클릭 시 해당 매장 정보 보여주기
		//이벤트(리스트 클릭 시)
		$(".menu").on("click", "li",function(){
			//리스트 activated
			$(".list-group-item").attr("class", "list-group-item");
			$(this).attr("class", "list-group-item activated");
			//해당 매장의 정보 보여주기
			var code = $(this).data("code");
			setStoreInfo(code);
		})
	
		//카카오맵 api	
		var mapContainer = document.getElementById('map'), // 지도를 표시할 div 
		mapOption = {
			center : new kakao.maps.LatLng(33.450701, 126.570667), // 지도의 중심좌표
			level : 3
		// 지도의 확대 레벨
		};

		// 지도를 생성합니다    
		var map = new kakao.maps.Map(mapContainer, mapOption);

		// 주소-좌표 변환 객체를 생성합니다
		var geocoder = new kakao.maps.services.Geocoder();
		//주소값을 가져옵니다.
		var address = $(".address").text();
		console.log("address : "+address);
		var name = $(".name").text();
		console.log("name : "+name);
		// 주소로 좌표를 검색합니다
		geocoder.addressSearch(address,function(result, status) {
														
							// 정상적으로 검색이 완료됐으면 
							if (status === kakao.maps.services.Status.OK) {
								console.log("검색 완료");
								var coords = new kakao.maps.LatLng(result[0].y,
										result[0].x);

								// 결과값으로 받은 위치를 마커로 표시합니다
								var marker = new kakao.maps.Marker({
									map : map,
									position : coords
								});

								// 인포윈도우로 장소에 대한 설명을 표시합니다
								var infowindow = new kakao.maps.InfoWindow(
										{
											content : '<div style="width:150px;text-align:center;padding:6px 0;">'+name+'</div>'
										});
								infowindow.open(map, marker);

								// 지도의 중심을 결과값으로 받은 위치로 이동시킵니다
								map.setCenter(coords);
							}
		});
		
		
		
		//list1 클릭 시
		$("#list1").click(function(){
			console.log("list1 clicked");
			$(".menu li").attr("class", "list-group-item");
			$(this).attr("class", "list-group-item activated");
			
			//사진 변경
			$(".store-img").attr("src", "/resources/store_img/jong-ro.jpg");
			//name 변경
			$(".name").text("스타벅스 더종로R점");
			//address 변경
			$(".address").text("서울특별시 종로구 종로2가 종로 51");
			
			searchMap();
			
	});
		//list2 클릭 시
		$("#list2").click(function(){
			console.log("list2 clicked");
			$(".menu li").attr("class", "list-group-item");
			$(this).attr("class", "list-group-item activated");
			
			//사진 변경
			$(".store-img").attr("src", "data:image/jpeg;base64,")
			//name 변경
			$(".name").text("스타벅스 신세계의정부점");
			//address 변경
			$(".address").text("경기 의정부시 평화로 525");
			
			searchMap();
			
	});
	
		//list3 클릭 시
		$("#list3").click(function(){
			console.log("list3 clicked");
			$(".menu li").attr("class", "list-group-item");
			$(this).attr("class", "list-group-item activated");
			
			//사진 변경
			$(".store-img").attr("src", "data:image/jpeg;base64,");
			//name 변경
			$(".name").text("스타벅스 서울숲역점");
			//address 변경
			$(".address").text("서울 성동구 왕십리로 50");
			
			searchMap();
			
	});
	
		//list4 클릭 시
		$("#list4").click(function(){
			console.log("list4 clicked");
			$(".menu li").attr("class", "list-group-item");
			$(this).attr("class", "list-group-item activated");
			
			//사진 변경
			$(".store-img").attr("src", "data:image/jpeg;base64,");
			//name 변경
			$(".name").text("스타벅스 용산아이파크몰점");
			//address 변경
			$(".address").text("서울 용산구 한강대로23길 55");
			
			searchMap();
			
	});
	
		//list5 클릭 시
		$("#list5").click(function(){
			console.log("list4 clicked");
			$(".menu li").attr("class", "list-group-item");
			$(this).attr("class", "list-group-item activated");
			
			//사진 변경
			$(".store-img").attr("src", "data:image/jpeg;base64,");
			//name 변경
			$(".name").text("스타벅스 판교역점");
			//address 변경
			$(".address").text("경기 성남시 분당구 대왕판교로606번길 58");
			
			searchMap();
			
	});
	
	//1) 코드를 받아 작동하는 메서드
	//2) 매장 정보를 받아옴
	//3) 매장 정보를 화면에 기입함
	//4) 기입한 정보를 기반으로 맵을 보여줌
	
	
	//카카오 맵 - 검색하여 지도에 표시
	function searchMap(){
		// 주소-좌표 변환 객체를 생성합니다
//		var geocoder = new kakao.maps.services.Geocoder();
		//주소값을 가져옵니다.
		address = $(".address").text();
		console.log("address : "+address);
		name = $(".name").text();
		console.log("name : "+name);
		// 주소로 좌표를 검색합니다
		geocoder.addressSearch(address,function(result, status) {
														
							// 정상적으로 검색이 완료됐으면 
							if (status === kakao.maps.services.Status.OK) {
								console.log("검색 완료");
								var coords = new kakao.maps.LatLng(result[0].y,
										result[0].x);

								// 결과값으로 받은 위치를 마커로 표시합니다
								var marker = new kakao.maps.Marker({
									map : map,
									position : coords
								});

								// 인포윈도우로 장소에 대한 설명을 표시합니다
								var infowindow = new kakao.maps.InfoWindow(
										{
											content : '<div style="width:150px;text-align:center;padding:6px 0;">'+name+'</div>'
										});
								infowindow.open(map, marker);

								// 지도의 중심을 결과값으로 받은 위치로 이동시킵니다
								map.setCenter(coords);
							}
		});
	}
	
})